package javastudy;

public class OperatorExam6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int b1 = (5 < 4) ? 50 : 40;
		
		System.out.println(b1);
		
		int age = 20;
		boolean isTwenty = (age >=20 && age<=29) ? true : false;
		System.out.println(isTwenty);
		
		int b2 = 0;
		if(5 > 4) {
			b2 = 50;
		}else {
			b2 = 40;
		}
		System.out.println(b2);

	}

}
