package javaStudy;

public class OperatorExam4 {
//비교연산자
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 10;
		int j = 10;
		
		System.out.println(i==j);
		System.out.println(i != j);
		System.out.println(i < j);
		System.out.println(i <=  j);
		System.out.println(i > j);
		System.out.println(i >= j);
	
		i += 10; // i = i + 10;
		System.out.println(i);
		
		System.out.println(i -= 5);
		System.out.println(i);
	
	
	}

}
